import React, { useEffect, useState } from 'react'
import {Link, useParams} from 'react-router-dom'
import axios from 'axios';

const OwnerHome = (props) => {
    const [owner, setOwner] = useState("");
    const [listing,setListing] = useState([]);

    const {id} = useParams();

    useEffect(() => {
        axios.get('http://localhost:8000/api/owners/' + id)
        .then(res => setOwner(res.data))
        .catch(err => console.log(err))
    },[])

    useEffect(() => {
        axios.get('http://localhost:8000/api/listings')
        .then(res => setListing(res.data))
        .catch(err => console.log(err))
    })

    const filteredListing = listing.filter((object) => object.owner === id)
    return(
        <div>
            <h1>Hello {owner.username}</h1>
            <h1>Your Listing:</h1>
            <h1>Address: {filteredListing[0].address}</h1>
            <h1>Photo URL: {filteredListing[0].photo_url}</h1>
            <h1>Description: {filteredListing[0].description}</h1>
        </div>
    )
}
export default OwnerHome
