import React, { useEffect, useState } from "react";
import { Link, useParams,useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import Carousel from "react-bootstrap/Carousel";
import './style.css'

const RenterHome = (props) => {
    const [renter, setRenter] = useState("");
    const [listing, setListing] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const { id } = useParams();

    const navigate = useNavigate();
    
    useEffect(() => {
        axios.get('http://localhost:8000/api/renters/' + id)
        .then(res => setRenter(res.data))
        .catch(err => console.log(err))
    },[])

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/listings")
            .then((res) => {
                setListing(res.data);
                setLoaded(true);
                console.log(listing);
            })
            .catch((err) => console.log(err));
    }, []);

    return (
        <div>
            <h1>Hello, {renter.username}</h1>
            <Carousel>
                {listing.map((listing,i)=>
                <Carousel.Item>
                    <img src={loaded && `${listing.photo_url}`} onClick ={ () =>{<Navigate to={"/"}/>}} />
                    <Carousel.Caption>
                        <h3 className="bookStay">Book this stay today!</h3>
                    </Carousel.Caption>
                </Carousel.Item>
                )}
            </Carousel>
        </div>
    );
};
export default RenterHome;
