import React, { useEffect, useState } from 'react'
import {Link, useParams} from 'react-router-dom'
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
const RenterHome = (props) => {
    const [renter, setRenter] = useState("");
    const [listing,setListing] = useState([]);
    const [loaded,setLoaded] = useState(false);
    const {id} = useParams();

    useEffect(()=>{
        axios.get('http://localhost:8000/api/listings')
        .then(res=>{
            setListing(res.data);
            setLoaded(true);
            console.log(listing)
        })
        .catch(err => console.log(err))
    },[]);
    const myCarouselElement = document.querySelector('#myCarousel')

const carousel = new bootstrap.Carousel(myCarouselElement, {
  interval: 2000,
  touch: false
})
    return(
        <div>
            <h1>Renter</h1>
            <div id="carouselExample" class="carousel slide">
                <div class="carousel-inner">
                    <div class="carousel-item active">
                        <img src={loaded && `${listing[0].photo_url}`} class="d-block w-100" alt="..."/>
                    </div>
                    <div class="carousel-item">
                        <img src={loaded && `${listing[1].photo_url}`} class="d-block w-100" alt="..."/>
                    </div>
                    <div class="carousel-item">
                        <img src={loaded && `${listing[2].photo_url}`} class="d-block w-100" alt="..."/>
                    </div>
                </div>
            <button class="carousel-control-prev" type="button" data-bs-target="#carouselExample" data-bs-slide="prev">
                <span class="carousel-control-prev-icon" aria-hidden="true"></span>
                <span class="visually-hidden">Previous</span>
            </button>
            <button class="carousel-control-next" type="button" data-bs-target="#carouselExample" data-bs-slide="next">
                <span class="carousel-control-next-icon" aria-hidden="true"></span>
                <span class="visually-hidden">Next</span>
            </button>
            </div>
        </div>
    )
}
export default RenterHome
