import React, { useEffect, useState,Navigate } from "react";
import { Link, useParams,useNavigate,useLocation } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import Carousel from "react-bootstrap/Carousel";
import './style.css'

const Listing = (props) => {
    const [listing, setListing] = useState([]);
    const [loaded, setLoaded] = useState(false);
    console.log(useParams())
    const { id } = useParams();
    const{renterid} = location.state
    const navigate = useNavigate();

    useEffect(() => {
        axios.get("http://localhost:8000/api/listings/" + id)
            .then((res) => {
                setListing(res.data);
                setLoaded(true);
                
            })
            .catch((err) => console.log(err));
    }, []);

    // useEffect(() =>{
    //     axios.post("http://localhost:8000/api/requests",{
    //         renter,
    //         listing,
    //         request_status

    //     })
    //     .then(res=>{
    //         console.log(res)
    //     })
    //     .catch(err => console.log(err))

    // },[])
    return (
        <div>
            <div className='topBar'>
                <div className="leftSide">
                    <Link to ={"/"} className="stayHome">StayHome</Link>
                </div>
                <div className="rightSide">
                <Link to="/">Logout</Link>
                </div>
            </div>
            <h1>Book this listing today!</h1>
            <img src={loaded && `${listing.photo_url}`} width="800"/>
            <h3>{listing.address}</h3>
            <div className="requestCheck">
            <button type="button" class="btn btn-success">Request to book</button>
            </div>
            <div className="botBar">
                <h1 className="botBarText">I am Bottom Bar</h1>
            </div>
        </div>
    );
};
export default Listing;
