import React, { useEffect, useState,Navigate } from "react";
import { Link, useParams,useNavigate } from "react-router-dom";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import './style.css'

const RenterHome = (props) => {
    const [renter, setRenter] = useState("");
    const [listing, setListing] = useState([]);
    const [loaded, setLoaded] = useState(false);
    const [request, setRequest] = useState([])
    const { id } = useParams();

    const navigate = useNavigate();
    
    useEffect(() => {
        axios.get('http://localhost:8000/api/renters/' + id)
        .then(res => setRenter(res.data))
        .catch(err => console.log(err))
    },[])

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/listings")
            .then((res) => {
                setListing(res.data);
                setLoaded(true);
            })
            .catch((err) => console.log(err));
    }, []);

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/requests")
            .then((res) => {
                setRequest(res.data);
            })
            .catch((err) => console.log(err));
    }, []);

    useEffect(()=>{

        for(let i = 0; i < filteredRequest.length;i++){
            console.log(filteredRequest[i].listing)
            const filteredPhotos = listing.filter((listing) => listing._id === filteredRequest[i].listing)
            console.log(filteredPhotos)
        }
    },[])
    
    const filteredRequest = request.filter((renter) => renter.renter === id);


    return (
        <div>
            <div className='topBar'>
                <div className="leftSide">
                    <Link to ={"/"} className="stayHome">StayHome</Link>
                </div>
                <div className="rightSide">
                <Link to="/">Logout</Link>
                </div>
            </div>
            <h1>Hello, {renter.username}</h1>
            <h1>Click on any home to view more details!</h1>
            <div  className="homeImageDiv">
                {listing.map((listing,i)=>
                    <div>
                        <Link key = {i}to={`/listing/${renter._id}/${listing._id}`}>
                            <img key = {i}className="homeImage" src={loaded && `${listing.photo_url}`} width={500}/>
                        </Link>
                        <h3>{listing.address}</h3>
                    </div>
                )}
            </div>
            <div className="statusBar">
                <div>
                    <h1>Your Requests</h1>
                    {filteredRequest.map((filteredRequest,i)=>
                    <p>Listing: {filteredRequest.listing}, Status: {filteredRequest.request_status}</p>
                    )}
                </div>
            </div>
            <div className="botBar">
                <h1>I am Bottom Bar</h1>
            </div>
        </div>
    );
};
export default RenterHome;
